#!/bin/python3
import socket

from scapy.config import conf
from scapy.layers.inet import IP, UDP, TCP
from scapy.packet import Raw
from scapy.sendrecv import sr1

conf.verb = 0

def do_traceroute(pkt):
    for i in range(1, 28):
        pkt[IP].ttl=i
        reply = sr1(pkt, verbose=0, timeout=3)
        if reply is None:
            print("{hoops} hops away: No reply".format(hoops=i))
        else:
            src = reply.src
            try:
                address = socket.gethostbyaddr(reply.src)
            except:
                address = None
            print("{hoops} hops away: {src} ({address})".format(hoops=i, src=src, address=address))


